import { HttpClient } from '@angular/common/http';
import { Component, Inject } from '@angular/core';
import { HttpClientModule } from '@angular/common/http';

@Component({
  selector: 'app-phone-book',
  templateUrl: './phone-book.component.html',
  styleUrls: ['./phone-book.component.css']
})
export class PhoneBookComponent {

  firstName: string = "";
  surname: string = "";
  phoneNumber: string = "";

  constructor(
    private http: HttpClient,
    @Inject('BASE_URL') private baseUrl: string
  ) {
  }
}

<!DOCTYPE html>
<html>
<script src="https://ajax.googleapis.com/ajax/libs/angularjs/1.6.9/angular.min.js"></script>
<body>

  <div ng-app="">

    <p>Input something in the input box:</p>

    <p>Name : <input type="text" ng-model="namfirstNamee" placeholder="Enter name here"></p>
    <h1>Hello {{firstName}}</h1>

    <p>Name : <input type="text" ng-model="surname" placeholder="Enter name here"></p>
    <h1>Hello {{surname}}</h1>

    <p>Name : <input type="text" ng-model="phoneNumber" placeholder="Enter name here"></p>
    <h1>Hello {{phoneNumber}}</h1>
  </div>

</body>
</html>
