import { HttpClient } from '@angular/common/http';
import { Component, Inject } from '@angular/core';
import { HttpClientModule } from '@angular/common/http';
import { AppService } from '../app.service';
import { PhoneBookEntry } from '../models/phone-book-entry';

@Component({
  selector: 'app-phone-book',
  templateUrl: './phone-book.component.html',
  styleUrls: ['./phone-book.component.css']
})
export class PhoneBookComponent {
  phoneBookEntry: PhoneBookEntry[] = [];
  phoneBookEntries?: PhoneBookEntry;


  constructor(
    private http: HttpClient,
    private appservice: AppService,
    // @Inject('BASE_URL') private baseUrl: string
  ) {

  }
  ngOnInit(): void {
    // fetch Students
    this.appservice.GetAllEntries()
      .subscribe(
        (successResponse) => {
          debugger
          this.phoneBookEntry = successResponse
        },
        (errorResponse) => {
          console.log(errorResponse);
        }
      );
  }
  
  // RemovePhonebookEntry(): void {
  //   // call student service to delete
  //   this.appservice.RemovePhonebookEntry(this.phoneBookEntry.id)
  //     .subscribe(
  //       (successResponse) => {
  //         this.phoneBookEntry = successResponse 
  //       },
  //       (errorResponse) => {
  //         console.log(errorResponse);
  //       }

  //       },
  //       (errorResponse) => {

  //       }
  //     )

  // }

  SaveNewPhonebookEntry(phoneBookEntry: PhoneBookEntry) {

    this.appservice.SaveNewPhonebookEntry(this.phoneBookEntry)
      .subscribe(
        (successResponse) => {
          this.phoneBookEntry = successResponse
        },(errorResponse) => {
          console.log(errorResponse);
        })
  }
}

<!DOCTYPE html>
<html>
<script src="https://ajax.googleapis.com/ajax/libs/angularjs/1.6.9/angular.min.js"></script>
<body>

  <div ng-app="">
    <div id="tableSection" style="margin-bottom: 5px;">
      <table style="border: 2px solid black; width: 100%">
        <tr>
          <th>Id</th>
          <th>First Name</th>
          <th>Surname</th>
          <th>Phone Number</th>
        </tr>

        <tbody>
          <tr>
            <td style="border: 2px solid black">Sample Id</td>
            <td style="border: 2px solid black">Sample Firstname</td>
            <td style="border: 2px solid black">Sample Surname</td>
            <td style="border: 2px solid black">Sample PhoneNo</td>
            <td style="border: 2px solid black">
              <div style="margin: auto; display: flex; justify-content: center;">
                <button style="margin-right: 5%">Edit</button>
                <button>Remove</button>
              </div>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
    <br />


    <div style="margin-top: 15px;">
      <span>Not seeing your info? Click <b>Register</b></span>
      <br />
      <button>Register</button>
    </div>
    <br />

  </div>

</body>
</html>
